def execute_command(image)->str:
	image_patch = ImagePatch(image)
	road_patches = image_patch.find("road")
	if len(road_patches) == 0:
		# If no road is found, query the image directly. Could happen if interpretation of "road" is vague.
		return image_patch.simple_query("What is surrounding the road?")
	road_patch = road_patches[0]
	surrounding_objects = []
	object_labels = ["tree", "building", "car", "person", "grass", "sign"]  # common objects that usually surround roads
	for object_label in object_labels:
		object_patches = image_patch.find(object_label)
		for object_patch in object_patches:
			if road_patch.overlaps_with(object_patch.left, object_patch.lower, object_patch.right, object_patch.upper):
				surrounding_objects.append(object_label)
	if not surrounding_objects:
		return "Nothing identifiable"
	else:
		return ", ".join(surrounding_objects)

