def execute_command(image)->str:
	image_patch = ImagePatch(image)
	motorcycle_patches = image_patch.find("motorcycle")
	if not motorcycle_patches:
		return "Cannot find a motorcycle."
	motorcycle_patch = motorcycle_patches[0]  # Assuming only one motorcycle in the image
	on_motorcycle_patches = []
	# Look for objects on the motorcycle
	for object_name in ["helmet", "bag", "box", "person", "jacket"]:
		patches = image_patch.find(object_name)
		for patch in patches:
			if patch.overlaps_with(motorcycle_patch.left, motorcycle_patch.lower, motorcycle_patch.right, motorcycle_patch.upper):
				on_motorcycle_patches.append(object_name)
	if not on_motorcycle_patches:
		return "Nothing is on the motorcycle."
	else:
		return ', '.join(on_motorcycle_patches)

