def execute_command(image)->str:
	image_patch = ImagePatch(image)
	stove_patches = image_patch.find("stove")
	if len(stove_patches) == 0:
		# If no stove is found, query the image directly with simple_query instead of returning a long string like "There is no stove."
		return image_patch.simple_query("What is on the stove?")
	stove_patch = stove_patches[0]
	# Searching for a generic object ("object") might be too broad and could return a lot of irrelevant results,
	# but if the stove has something specific on it, it should return that
	object_patches = stove_patch.find("object")
	if len(object_patches) == 0:
		return "nothing"
	# If there's more than one object on the stove, return the most centrally located one
	object_patches.sort(key=lambda patch: (patch.width - patch.horizontal_center)**2 + (patch.height - patch.vertical_center)**2)
	return object_patches[0].simple_query("What is this object?")

