	# First we check if there is a woman doing an art project
	art_project_patches = image_patch.find("art project")
	if len(art_project_patches) == 0:
		return "no art project is visible"

	# Then we check for the presence of a woman in those patches
	for project_patch in art_project_patches:
		if project_patch.exists('woman'):
			woman_patch = project_patch.find('woman')[0]
			break
	else:
		return "no woman doing art project visible"

	# If a woman is found, check for other people
	if woman_patch.verify_property('woman', 'alone'):
		return "alone"
	else:
		return "others are involved"

