	cone_properties = [cone_patch.simple_query("What are the properties of these cones?") for cone_patch in cone_patches]
	# Then we look for the property that is present in all cones
	common_properties = set(cone_properties[0].split(", "))
	for properties in cone_properties[1:]:
		common_properties.intersection_update(set(properties.split(", ")))
	if len(common_properties) == 0:
		return "nothing"
	return ", ".join(common_properties)
